import styled from "styled-components";
import {Header} from "antd/lib/layout/layout";
import {Typography} from "antd";
import {Content} from "antd/es/layout/layout";

export const ContentComponentStyled = styled(Content)`
  //background-color: #A7EBDC;
  height: 78vh;
  display: flex;
  justify-content: space-between;

  .example-item__content--bg-multiple {

    background: linear-gradient(to right, rgb(58, 170, 218), transparent);
    background-color: rgb(203, 54, 131);
    background-image: image(images/contentImage.jpg);
    background-repeat: no-repeat;
    background-position: center;
  }

`
