import styled from "styled-components";

interface PasswordStyledProps {
    valid: boolean;

}

export const PasswordStyled = styled.div`
  font-size: 20px;
  border-radius: inherit;
  padding: 5px;
  border: 2px solid ${(p: PasswordStyledProps) => p.valid ? 'green' : 'red'};
`
