import {createEffect, createEvent, createStore} from 'effector'
import {db} from "../../DAL/firebase/firebaseInit";
import {collection, getDocs} from "firebase/firestore";


export interface User {
    name: string | null
    password: string | null
}

let mockUsers: User[] = [{
    name: 'PressF',
    password: "12345"
}, {
    name: 'PressA',
    password: "54321"
},
]

export const checkUser = createEvent<User>()
const addUser = createEvent<User>()

//generic
export const $users = createStore<User[]>(mockUsers)

$users.watch(status => {
    console.log(status)
})
//наши хэшированые данные юзеров, хранение текущего введенного значения

export const deleteUserHeh = createEvent()
export const deletePasswordHeh = createEvent()
export const saveUsername = createEvent<string>()
export const saveRememberChange = createEvent<boolean>()

export const $currentRemember = createStore(true).on(
    saveRememberChange, (state, value) => {
        return value
    }
)

export const $currentUsername = createStore('').on(
    saveUsername, (state, userName) => {
        return userName
    })
    .on(deleteUserHeh, (state, userName) => {
        return ''
    })

export const savePassword = createEvent<string>()

export const $currentPassword = createStore('').on(
    savePassword, (state, Password) => {
        return Password
    })
    .on(deletePasswordHeh, (state, password ) => {
        return ''
    })
