import React from 'react';
import '../App.css';
import {HomeStyled} from "./Home-styled";
import {TextStyled} from "../styledComponent/TextStyled";
import {TextFilledStyled} from "../styledComponent/TextFilled-styled";
import {ButtonFilledStyled} from '../styledComponent/ButtonFilled-styled';
import {ButtonStyled} from '../styledComponent/Button-styled';
import {useStore} from "effector-react/effector-react.mjs";
import {currentUser} from "../BLL/effector/usersStoreLogined";

//условие: если есть залогиенный пользователь --> выводим его имя, если нет --> вывести сообщение
const Home: React.FC = () => {
    const loggedUser = useStore(currentUser)
    return (
        <HomeStyled>
            <TextFilledStyled>

                <TextStyled textSize={25} fontWeight={'normal'} color={'Black'}>
                    {loggedUser.name ? `${loggedUser.name}` : " Log in to the system to continue"}
                </TextStyled>

                <TextStyled textSize={25} color={'black'} fontWeight={'normal'}>
                    {checked ? 'yes' : 'no'}
                </TextStyled>
            </TextFilledStyled>

        </HomeStyled>

    )
}


export default Home;
