import {createEffect, createEvent, createStore} from 'effector'
import {db} from "../../DAL/firebase/firebaseInit";
import {collection, getDocs} from "firebase/firestore";

interface User {
    name: string,
    password: string,
}

let mockUsers: User[] = [{
    name: 'PressF',
    password: "12345"
}, {
    name: 'PressA',
    password: "54321"
},
]

const checkUser = createEvent<User>()

//generic
export const $users = createStore(mockUsers)
    .on(checkUser, (usersState, user: User) => usersState.some((u:User) => u.name === user.name && u.password===user.password))

$users.watch(status => {
    console.log(status)
})
