import styled from "styled-components";

interface UserStyledProps {
    isBorder: boolean;
    valid: boolean;

}

export const UserStyled = styled.div`
  font-size: 20px;
  border-radius: inherit;
  padding: 5px;
  border-color: ${(p: UserStyledProps) => p.valid ? 'green' : 'red'};
  ${(p: UserStyledProps) => p.isBorder && `border: 2px solid`}
`
