/**
 hydrate state on client

 const root = createDomain()
 hydrate(root, {
  values: window.__initialState__
})

 */
export function hydrate(domain: Domain | Scope, {values}: {values: any}) {
  assert(isObject(values), 'values property should be an object')
  const normalizedValues = normalizeValues(values)
  const valuesSidList = Object.getOwnPropertyNames(normalizedValues)
  const storeNodes: Node[] = []
  const storeValues: any[] = []
  let forkPage: Scope
  let traverseTarget: Node
  let needToAssign: true | void
  if (is.scope(domain)) {
    forkPage = domain
    needToAssign = true
    assert(forkPage.cloneOf, 'scope should be created from domain')
    traverseTarget = getGraph(forkPage.cloneOf)
  } else if (is.domain(domain)) {
    traverseTarget = getGraph(domain)
  } else {
    assert(false, 'first argument of hydrate should be domain or scope')
  }
  traverseStores(traverseTarget!, (node, sid) => {
    // forkPage.sidIdMap[sid] = node.scope.state.id
    if (includes(valuesSidList, sid)) {
      add(storeNodes, node)
      add(storeValues, normalizedValues[sid])
    }
  })
  launch({
    target: storeNodes,
    params: storeValues,
    scope: forkPage!,
  })
  if (needToAssign) {
    Object.assign(forkPage!.sidValuesMap, normalizedValues)
  }
}
import {createDefer} from '../defer'
import {is} from '../is'
import {assert} from '../throw'
import {launch, forkPage} from '../kernel'
import type {Scope} from '../unit.h'

/** bind event to scope */
export function scopeBind(unit: any, {scope}: {scope?: Scope} = {}) {
  assert(
    scope || forkPage,
    'scopeBind cannot be called outside of forked .watch',
  )
  const savedForkPage = scope || forkPage!
  return is.effect(unit)
    ? (params: any) => {
        const req = createDefer()
        launch({
          target: unit,
          params: {
            params,
            req,
          },
          scope: savedForkPage,
        })
        return req.req
      }
    : (params: any) => {
        launch({target: unit, params, scope: savedForkPage})
        return params
      }
}
import type {Scope, Store} from '../unit.h'
import {forIn, includes} from '../collection'
import {assert} from '../throw'
import {traverseStores} from './util'
import {getGraph, getMeta} from '../getter'

/**
 serialize state on server
 */
export function serialize(
  scope: Scope,
  config: {ignore?: Array<Store<any>>; onlyChanges?: boolean} = {},
) {
  const ignoredStores = config.ignore ? config.ignore.map(({sid}) => sid) : []
  const result = {} as Record<string, any>
  forIn(scope.sidValuesMap, (value, sid) => {
    if (includes(ignoredStores, sid)) return
    const id = scope.sidIdMap[sid]
    // if (!scope.changedStores.has(id)) return
    if (id && id in scope.reg) {
      result[sid] = scope.reg[id].current
    } else {
      result[sid] = value
    }
  })
  if ('onlyChanges' in config && !config.onlyChanges) {
    assert(scope.cloneOf, 'scope should be created from domain')
    traverseStores(getGraph(scope.cloneOf), (node, sid) => {
      if (
        !(sid in result) &&
        !includes(ignoredStores, sid) &&
        !getMeta(node, 'isCombine') &&
        getMeta(node, 'serialize') !== 'ignore'
      )
        result[sid] = scope.getState(node as any)
    })
  }
  return result
}
export const observableSymbol: any =
  (typeof Symbol !== 'undefined' && Symbol.observable) || '@@observable'
import {kind} from './index.h'
import {DOMAIN, STORE, EVENT, EFFECT, SCOPE} from './tag'
import {isObject, isFunction} from './is'
import {Domain, Effect, Event, Scope, Store} from './unit.h'

export const unit = (obj: any) =>
  (isFunction(obj) || isObject(obj)) && 'kind' in obj

const is = (type: kind) => (obj: any) => unit(obj) && obj.kind === type

export const store = is(STORE) as (value: any) => value is Store<unknown>
export const event = is(EVENT) as (value: any) => value is Event<unknown>
export const effect = is(EFFECT) as (
  value: any,
) => value is Effect<unknown, unknown, unknown>
export const domain = is(DOMAIN) as (value: any) => value is Domain
export const scope = is(SCOPE) as (value: any) => value is Scope
export * as is from './validate'
import {forEach} from './collection'
import {assert} from './throw'

export const isObject = (value: any) =>
  typeof value === 'object' && value !== null
export const isFunction = (value: any) => typeof value === 'function'

export const isVoid = (value: any) => value === undefined

export const assertObject = (value: any) =>
  assert(
    isObject(value) || isFunction(value),
    'expect first argument be an object',
  ) // or function

const assertNodeSetItem = (
  value: any,
  method: string,
  valueName: string,
  reason: string,
) =>
  assert(
    !(
      (!isObject(value) && !isFunction(value)) ||
      (!('family' in value) && !('graphite' in value))
    ),
    `${method}: expect ${valueName} to be a unit (store, event or effect)${reason}`,
  )

export const assertNodeSet = (
  value: any,
  method: string,
  valueName: string,
) => {
  if (Array.isArray(value)) {
    forEach(value, (item, i) =>
      assertNodeSetItem(item, method, `${i} item of ${valueName}`, ''),
    )
  } else {
    assertNodeSetItem(value, method, valueName, ' or array of units')
  }
}
const idCount = () => {
  let id = 0
  return () => `${++id}`
}

export const nextUnitID = idCount()
