import React, {useState} from 'react';
import '../App.css';
import {TextStyled} from '../styledComponent/TextStyled';
import {Checkbox, Input} from 'antd';
import {LoginStyled} from "./Login-styled";
import {UserStyled} from "./login/User-styled";
import {UserAllStyled} from "./login/UserAll-styled";
import {PasswordAllStyled} from "./Password/PasswordAll-styled";
import {ButtonFilledStyled} from '../styledComponent/ButtonFilled-styled';
import {ButtonStyled} from '../styledComponent/Button-styled';
import {$currentPassword, $currentUsername, $users, savePassword, saveUsername} from "../BLL/effector/usersStore";
import {useStore} from "effector-react";
import {currentUser, rememberUser} from "../BLL/effector/usersStoreLogined";
import {Link} from "react-router-dom";
import {InputFilledStyled} from '../styledComponent/InputFilled-styled';
import {useHistory} from "react-router-dom";

const Login: React.FC = () => {
    const username = useStore($currentUsername)
    const password = useStore($currentPassword)
    const mockUsers = useStore($users)
    const loggedUser = useStore(currentUser)

    const history = useHistory();

    const [isValid, setValid] = useState(true)

    //сохранение введенного значения логина и пароля в строки логина
    const handleUsernameChange = (e: { target: { value: React.SetStateAction<string> } }) => {
        saveUsername(e.target.value as string)
    }
    const handlePasswordChange = (e: { target: { value: React.SetStateAction<string> } }) => {
        savePassword(e.target.value as string)
    }

//сравнение логина и пароля с моками и с друг другом
    const checkUserRegistered = () => {
        const existUser = mockUsers?.find((u) => u.name === username && u.password === password)
        if (existUser) {
            rememberUser(existUser)
            history.push("/home")
        } else {
            setValid(false)
            
        }

    };
    const newAccount = () => {
        history.push('/newAccount')
    }
    //разрешенные значения для ввода в строку username
    const isStringValid = (string: string): boolean => {
        /*
          Usernames can only have:
          - Lowercase Letters (a-z)
          - Numbers (0-9)
          - Dots (.)
          - Underscores (_)
        */
        const res = /^[A-Za-z0-9_\.]+$/.exec(string);
        return !!res;
    }
    //рамка подсвечивается при вводе любых данных  в поля, залинковать на кнопу submit

    return (
        <LoginStyled valid={isValid}>
            <InputFilledStyled justifycontent={"space-between"} width={100} height={194}>
                <UserAllStyled>
                    <UserStyled valid={isStringValid(username)}>
                        <TextStyled textSize={20} color={'black'} fontWeight={"bold"}>
                            Username
                        </TextStyled>
                        <Input value={username} onChange={handleUsernameChange}/>
                    </UserStyled>
                </UserAllStyled>

                <PasswordAllStyled>
                    <UserStyled valid={isStringValid(password)}>
                        <TextStyled textSize={20} color={'black'} fontWeight={'bold'}>
                            Password
                        </TextStyled>
                        <Input type={'password'} value={password} onChange={handlePasswordChange}/>
                    </UserStyled>
                </PasswordAllStyled>
            </InputFilledStyled>


            <ButtonFilledStyled width={23} height={30} justifycontent={'space-between'}>

                <Checkbox/>
                <TextStyled textSize={17} color={'black'} fontWeight={'normal'}>
                    Remember me
                </TextStyled>

            </ButtonFilledStyled>

            <ButtonFilledStyled width={37} height={30} justifycontent={'space-between'}>
                <ButtonStyled onClick={checkUserRegistered}>
                    Submit
                </ButtonStyled>
                <ButtonStyled onClick={newAccount}>
                    New Account
                </ButtonStyled>

            </ButtonFilledStyled>


        </LoginStyled>

    )
}
//СДЕЛАТЬ КРАСНУЮ РАМКУ ПРИ НЕПРАВИЛЬНОМ ВВОДЕ ЛОГИНА, ПРИ ПРАВИЛЬНОМ ПЕРЕЙТИ В HOME

export default Login;

//добавить вывод сообщения о неправильно введенном логине и пароле
