import React from 'react';
import '../App.css';
import {Button} from "antd";
import {HeaderStyled} from "../styledComponent/Header-styled";
import {ButtonStyled} from "../styledComponent/Button-styled";
import {ButtonFilledStyled} from "../styledComponent/ButtonFilled-styled";
import {TextStyled} from '../styledComponent/TextStyled';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
} from 'react-router-dom';
import {createApi, createStore} from "effector";
import {useStore} from "effector-react/effector-react.mjs";

const $counter = createStore(0)

const {increment, decrement} = createApi($counter, {
    increment: state => state + 1,
    decrement: state => state - 1,
})

const HeaderComponent: React.FC = () => {
    const counter = useStore($counter)


    return (
        <HeaderStyled>
            <TextStyled textSize={25} color={'white'} fontWeight={'normal'}>
                Microblog
                {counter}
            </TextStyled>

            <ButtonFilledStyled width={12} height={30} justifycontent={'space-between'}>
                <ButtonStyled type="default">
                    <Link to="/login">Log In</Link>
                </ButtonStyled>

                <ButtonStyled type="default">
                    <Link to="/home">Home</Link>
                </ButtonStyled>
            </ButtonFilledStyled>

        </HeaderStyled>
    )
}


export default HeaderComponent;
