import React from 'react';
import '../App.css';
import { MainStyled } from '../styledComponent/Main-styled';
import { TextStyled } from '../styledComponent/TextStyled';
import ReactDOM from "react-dom";
var Recaptcha = require('react-recaptcha');


const MainComponent: React.FC = () => {

    let recaptchaInstance: { reset: () => void; };

// create a reset function
    const resetRecaptcha = () => {
        recaptchaInstance.reset();
    };
    return (
        <MainStyled>
            <TextStyled textSize={20} color={'white'} fontWeight={'bold'}>
            </TextStyled>
            <div>
                <Recaptcha
                    ref={(e: { reset: () => void; }) => recaptchaInstance = e}
                    sitekey="xxxxxxxxxxxxxxxxxxxx"
                />
                <button
                    onClick={resetRecaptcha}
                >
                    Reset
                </button>
            </div>
        </MainStyled>
    )
}



export default MainComponent;
