import {createEffect, createEvent, createStore} from 'effector'
import {db} from "../../DAL/firebase/firebaseInit";
import {collection, getDocs} from "firebase/firestore";


export interface UsersStoreState {
    users: Map<string, string> | null
}


const fetchUsers = createEffect(async () => {
    const querySnapshot = await getDocs(collection(db, "users"));
    querySnapshot.forEach((doc) => {
        console.log(`${doc.id} => ${doc.data()}`);
    });
    return querySnapshot
})

export type UserStore = UsersStoreState
//generic
export const $users = createStore<UserStore>({users: null})
    .on(fetchUsers.doneData, (usersStore, response) => {
        users:response
    })

$users.watch(status => {
    console.log(status)
})
