import React from 'react';
import '../App.css';
import {currentUser, deleteUser, rememberUser} from "../BLL/effector/usersStoreLogined";
import {NewAccountStyled} from "./submit/NewAccount-styled";
import { UserStyled } from './login/User-styled';
import {Input} from "antd";
import {useStore} from "effector-react";
import {$currentUsername, saveUsername} from "../BLL/effector/usersStore";
import { ButtonFilledStyled } from '../styledComponent/ButtonFilled-styled';
import { ButtonStyled } from '../styledComponent/Button-styled';
import { Link } from 'react-router-dom';
import { TextStyled } from '../styledComponent/TextStyled';
import { InputFilledStyled } from '../styledComponent/InputFilled-styled';
import {useHistory} from "react-router-dom";


const NewAccount: React.FC = () => {
    const handleUsernameChange = (e: { target: { value: React.SetStateAction<string> } }) => {
        saveUsername(e.target.value as string)
    }
    const handleLogoutUser = () => deleteUser()
    const username = useStore($currentUsername)
    const history = useHistory()
    const back = () => {
        history.push('/login')
    }
    const isStringValid = (string: string): boolean => {
        /*
          Usernames can only have:
          - Lowercase Letters (a-z)
          - Numbers (0-9)
          - Dots (.)
          - Underscores (_)
        */
        const res = /^[A-Za-z0-9_\.]+$/.exec(string);
        return !!res;
    }
    return (
        <NewAccountStyled>
            <InputFilledStyled width={100} height={194} justifycontent={"space-between"}>
                <UserStyled valid={isStringValid(username)}>
                    <TextStyled textSize={20} color={"black"} fontWeight={"bold"}>
                        Username
                    </TextStyled>
                    <Input placeholder={'your username'} value={username} onChange={handleUsernameChange}/>
                </UserStyled>

                <UserStyled valid>
                    <TextStyled textSize={20} color={"black"} fontWeight={"bold"}>
                        Password
                    </TextStyled>
                    <Input placeholder={'your password'}/>
                </UserStyled>
            </InputFilledStyled>
            <TextStyled textSize={20} color={'black'} fontWeight={'lighter'}>
                You can use only A-Z, a-z, 0-9 symbols
            </TextStyled>


            <ButtonFilledStyled justifycontent={"space-between"} height={30} width={27} >
                <ButtonStyled onClick={back}>
                    Back
                </ButtonStyled>

                <ButtonStyled>
                    Create
                </ButtonStyled>
            </ButtonFilledStyled>
        </NewAccountStyled>
    )
}


export default NewAccount;
